import React from "react";
import ReactDom from "react-dom";

//CSS
import "./index.css";

//setup vars
const firstBook = {
  img: "https://m.media-amazon.com/images/W/WEBP_402378-T2/images/I/51nvk2Smg3L._SX218_BO1,204,203,200_QL40_FMwebp_.jpg",
  title: "The Bear and The Fern",
  author: "Shashank Pandey",
};

const secondBook = {
  img: "https://m.media-amazon.com/images/W/WEBP_402378-T2/images/I/51-uspgqWIL._SY291_BO1,204,203,200_QL40_FMwebp_.jpg",
  title: "Atomic Habits",
  author: "James Clear",
};

function Booklist() {
  return (
    <section className="booklist">
      <Book
        img={firstBook.img}
        title={firstBook.title}
        author={firstBook.author}
      />
      <Book
        img={secondBook.img}
        title={secondBook.title}
        author={secondBook.author}
      />
    </section>
  );
}

const Book = (props) => {
  console.log(props);
  return (
    <article className="book">
      <img src={props.img} alt=" " />
      <h1>{props.title.toUpperCase()}</h1>
      <h5>{props.author}</h5>
    </article>
  );
};

ReactDom.render(<Booklist />, document.getElementById("root"));
